              , qna_content, qna_type, qna_secret
              , qna_hit, qna_date)
         VALUES(qna_seq.nextval,#{mem_no},#{qna_title}
               ,#{qna_content},'양도', #{qna_secret}
               ,#{qna_hit}, to_char(sysdate,'YYYY-MM-DD'))
